import { DefaultControl } from './Control.default';
export function DefaultTextEdit(item) {
    DefaultControl(item);
    item.autowrap_mode ?? (item.autowrap_mode = 3);
    item.caret_blink ?? (item.caret_blink = false);
    item.caret_blink_interval ?? (item.caret_blink_interval = 0.65);
    item.caret_draw_when_editable_disabled ?? (item.caret_draw_when_editable_disabled = false);
    item.caret_mid_grapheme ?? (item.caret_mid_grapheme = false);
    item.caret_move_on_right_click ?? (item.caret_move_on_right_click = true);
    item.caret_multiple ?? (item.caret_multiple = true);
    item.caret_type ?? (item.caret_type = 0);
    item.clip_contents ?? (item.clip_contents = true);
    item.context_menu_enabled ?? (item.context_menu_enabled = true);
    item.custom_word_separators ?? (item.custom_word_separators = "");
    item.deselect_on_focus_loss_enabled ?? (item.deselect_on_focus_loss_enabled = true);
    item.drag_and_drop_selection_enabled ?? (item.drag_and_drop_selection_enabled = true);
    item.draw_control_chars ?? (item.draw_control_chars = false);
    item.draw_spaces ?? (item.draw_spaces = false);
    item.draw_tabs ?? (item.draw_tabs = false);
    item.editable ?? (item.editable = true);
    item.empty_selection_clipboard_enabled ?? (item.empty_selection_clipboard_enabled = true);
    item.focus_mode ?? (item.focus_mode = 2);
    item.highlight_all_occurrences ?? (item.highlight_all_occurrences = false);
    item.highlight_current_line ?? (item.highlight_current_line = false);
    item.indent_wrapped_lines ?? (item.indent_wrapped_lines = false);
    item.language ?? (item.language = "");
    item.middle_mouse_paste_enabled ?? (item.middle_mouse_paste_enabled = true);
    item.minimap_draw ?? (item.minimap_draw = false);
    item.minimap_width ?? (item.minimap_width = 80);
    item.mouse_default_cursor_shape ?? (item.mouse_default_cursor_shape = 1);
    item.placeholder_text ?? (item.placeholder_text = "");
    item.scroll_fit_content_height ?? (item.scroll_fit_content_height = false);
    item.scroll_fit_content_width ?? (item.scroll_fit_content_width = false);
    item.scroll_horizontal ?? (item.scroll_horizontal = 0);
    item.scroll_past_end_of_file ?? (item.scroll_past_end_of_file = false);
    item.scroll_smooth ?? (item.scroll_smooth = false);
    item.scroll_v_scroll_speed ?? (item.scroll_v_scroll_speed = 80.0);
    item.scroll_vertical ?? (item.scroll_vertical = 0.0);
    item.selecting_enabled ?? (item.selecting_enabled = true);
    item.shortcut_keys_enabled ?? (item.shortcut_keys_enabled = true);
    item.structured_text_bidi_override ?? (item.structured_text_bidi_override = 0);
    item.structured_text_bidi_override_options ?? (item.structured_text_bidi_override_options = []);
    //syntax_highlighter
    item.text ?? (item.text = "");
    item.text_direction ?? (item.text_direction = 0);
    item.use_custom_word_separators ?? (item.use_custom_word_separators = false);
    item.use_default_word_separators ?? (item.use_default_word_separators = true);
    item.virtual_keyboard_enabled ?? (item.virtual_keyboard_enabled = true);
    item.wrap_mode ?? (item.wrap_mode = 0);
}
